"use strict"
function numOfDaysCostCalc() { let numOfDaysField = document.getElementById("numOfDaysField"); let numOfDaysCost = numOfDaysField.value * 29.99; return numOfDaysCost }

function optionsCostCalc() { let tollTagCBox = document.getElementById("tollTagCBox").checked; let gpsCBox = document.getElementById("gpsCBox").checked; let roadsideCBox = document.getElementById("roadsideCBox").checked; let optionsCost = 0; if (tollTagCBox) { optionsCost += 3.95 } if (gpsCBox) { optionsCost += 2.95 } if (roadsideCBox) { optionsCost += 2.95 } return optionsCost }

function surchargeCostCalc() { let under25NoRB = document.getElementById("under25No"); let under25YesRB = document.getElementById("under25Yes"); let under25sc; if (under25YesRB.checked) { under25sc = 0.3 } else { under25sc = 0 } return under25sc }

function totalPriceCalc() { let numOfDaysFinal = numOfDaysCostCalc(); let optionsFinal = optionsCostCalc(); let surchargeAmt = surchargeCostCalc(); let surchargeFinal = numOfDaysFinal * surchargeAmt;
    document.getElementById("carRentalCostField").value = numOfDaysFinal.toFixed(2);
    document.getElementById("optionsCostField").value = optionsFinal.toFixed(2);
    document.getElementById("under25SurchargeField").value = surchargeFinal.toFixed(2); let totalCost = numOfDaysFinal + optionsFinal + surchargeFinal;
    document.getElementById("totalCostField").value = totalCost.toFixed(2) }
window.onload = function() { const totalCostBtn = document.getElementById("totalCostBtn");
    totalCostBtn.onclick = totalPriceCalc }